Page._content:
.clock{
    position: absolute;
    color: #ffffff;
    list-style: none;
    padding: 0;
    width: 40vh;
    height: 40vh;
    left: 10%;
    top: 25%;
    line-height: 20px;
    user-select: none;
}

.clock .date{
    position: absolute;
    z-index: 1;
    width: 100%;
    height: 20px;
    text-align: center;
    top: 46%;
    left: 0;
}
.clock .hour{
    position: absolute;
    z-index: 3;
    width: 90%;
    height: 20px;
    top: 46%;
    left: 5%;
    transition: transform 0.3s ease-in-out 0s;
    transform:rotate(0deg);
}
.clock .hour>div{
    position: absolute;
    width: 100%;
    right: 0;
    top: 0;
    transition: transform 1s ease-in-out 0s;
    transform:rotate(0deg);
}
.clock .hour>div>div{
    float: right;
    width: 60px;
    text-align: right;
}
.clock .minute{
    position: absolute;
    z-index: 4;
    width: 150%;
    height: 20px;
    top: 46%;
    left: -25%;
}
.clock .sec{
    position: absolute;
    z-index: 5;
    width: 200%;
    height: 20px;
    top: 46%;
    left: -48%;
}

.clock>hr{
    height: 0;
    width: 0;
    position: absolute;
    z-index: 1;
    border: #ffffff solid 0;
    border-bottom-width: 1px;
    margin: 10px 0 0 0;
    left: 50%;
    top: 50%;
    transition: width 0.3s ease-in-out 0s;
    overflow: visible;
}
.clock>hr.active:before{
    content: '';
    display: block;
    width: 5px;
    height: 5px;
    border-radius: 50%;
    background-color: yellow;
    top: -2px;
    left: 0;
    position: absolute;
}

