Page.content:
/* è‡ªå®šä¹‰css */
#page-header{
    overflow: hidden;
}
#page-header::before{    
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, .8);;
    z-index: 1;
    filter: blur(3px);
    background: url('../img/fairy.jpg') no-repeat;
    background-size: 100% 100%;
}
#page-header[page-size=min]::before{
    background: url('../img/gotsisop.jpg') no-repeat;
    filter: blur(1px);
    background-size: 100%;
}


/* pwaç›¸å…³ */
.app-refresh {
    position: fixed;
    top: -2.2rem;
    left: 0;
    right: 0;
    z-index: 99999;
    padding: 0 1rem;
    font-size: 15px;
    height: 2.2rem;
    transition: all 0.3s ease;
}
.app-refresh-wrap {
    display: flex;
    color: #fff;
    height: 100%;
    align-items: center;
    justify-content: center;
}

.app-refresh-wrap span {
    color: #fff;
    text-decoration: underline;
    cursor: pointer;
}
.aplayer-flod{
    position: fixed;
    width: 1.5rem;
    height: 1.5rem;
    border-radius: 50%;
    top: 90%;
    z-index: 1;
    /* border: 2px solid #ccc; */
    background: url('/img/cloudmusic.png') no-repeat center;
    background-size: cover;
    transition: all .6s ease-in-out;
    cursor: pointer;
}
.aplayer-flod[left=true]:hover{
    background: url('/img/right.png') no-repeat center;
    background-size: cover;
    transform: rotate(360deg);
}
.aplayer-flod[left=false]:hover{
    background: url('/img/right.png') no-repeat center;
    background-size: cover;
    transform: rotate(180deg);
}
.aplayer-flod[flod=false]{
    display: none;
}
.aplayer-flod[left=true]{
    left: 2%;
}
.aplayer-flod[left=false]{
    right: 0;
}
.aplayer-container{
    position: fixed;
    /* width: 14rem; */
    width: 18rem;
    top: 80%;
    z-index: 6;
    border: 3px solid #fff;
    border-radius: 5px;
    color: var(--font-color);
    user-select: none;
    transition: width .3s ease-in-out;
    overflow: hidden;
    white-space: nowrap;
    background: var(--card-bg);
    box-shadow: var(--card-hover-box-shadow);
}
.aplayer-container::before{
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(255, 255, 255,.3);
    z-index: -1;
    filter: blur(6px);
}
.aplayer-container p {
    display: inline-block;
    width: 92%;
    margin: 0;
    padding-left: 7px;
    cursor: move;
}
.aplayer-container i{
    margin-right: 5px;
}
.aplayer-container .closemusic{
    cursor: pointer;
}
.aplayer{
    border-radius: 3px;
}

.layout_post>#post{
    background: rgba(255, 255, 255, 0)
}
#site-info {
    right: -10%;
    top: 70%!important;
    z-index: 1;
}

