    constructor() {
        if(instance) {
            return instance;
        }else{
            instance = this;
            this.init();
        }
    }

    init() {
        console.log('init user events');
        this.checkPageSize();
        $(window).resize(() =>{
            //æ‰§è¡Œä»£ç å—
            this.checkPageSize();
         });
    }
    checkPageSize() {
        if($(window).width() <= 720) {
            if(timer){
                timer.remove();
                timer = null;
            }

            $('#page-header').attr('page-size','min');
        }else{
            new Timepiece();
            $('#page-header').attr('page-size', 'normal' );
        }
    }
}

