    setTheme(index, xhr)  {
        const image = new Image();
        const _this = this;
        const colorThief = new ColorThief();
        if (!_this.ap.list.audios[index].theme) {
            xhr.onload = function() {
                let coverUrl = URL.createObjectURL(this.response);
